                       if (!sib || !sib.length) {                            sib = body.find('input').eq(0);                        }                        sib.select().focus();                    }                    function onKeyDown(e) {                        var key = e.which;                        if (key === 9 || (key >= 48 && key <= 57) || (key >= 96 && key <= 105)) {                            return true;                        }                        e.preventDefault();                        return false;                    }                    function onFocus(e) {                        $(e.target).select();                    }                    body.on('keyup', 'input', goToNextInput);                    body.on('keydown', 'input', onKeyDown);                    body.on('click', 'input', onFocus);                })            </script><script src="assets/libs/bootstrap/js/bootstrap.bundle.min.js"></script><script src="assets/libs/metismenu/metisMenu.min.js"></script><script src="assets/libs/simplebar/simplebar.min.js"></script><script src="assets/libs/node-waves/waves.min.js"></script><script src="assets/js/app.js"></script><script>                //  debugger;                let timerOn = true;                function timer(remaining) {                    var m = Math.floor(remaining / 60);                    var s = remaining % 60;                    m = m < 10 ? '0' + m : m;                    s = s < 10 ? '0' + s : s;                    document.getElementById('timer').innerHTML = m + ':' + s;                    remaining -= 1;                    if (remaining >= 0 && timerOn) {                        setTimeout(function() {                        document.getElementById("hide1").style.display = "block";                        timer(remaining);                        }, 1000);                        return;                    }                    if (!timerOn) {                     return;                    }                    alert('Timeout for otp');                    document.getElementById("hide1").style.display = "none";                    document.getElementById("hide2").style.display = "block";                }                timer(120);            </script></body></html>